<template>  <view class="container">    <h1>Sign up</h1>    <view class="subtext">      Already have an account? <a href="#">Sign in</a>    </view>    <label for="email">Email address</label>    <input type="email" id="email" v-model="email" placeholder="you@example.com" />    <label for="password">Password</label>    <input      :type="showPassword ? 'text' : 'password'"      id="password"      v-model="password"      placeholder="Enter password"    />    <view class="eye-icon" @click="togglePassword"></view>    <button class="primary" @click="signUp">Sign up</button>    <view class="divider">Or continue with</view>    <view class="alt-login" @click="loginWith('Google')">      <img src="https://placehold.co/24x24" alt="Google" />      Continue with Google    </view>    <view class="alt-login" @click="loginWith('Facebook')">      <img src="https://placehold.co/24x24" alt="Facebook" />      Continue with Facebook    </view>    <view class="alt-login" @click="loginWith('Phone')">      <img src="https://placehold.co/24x24" alt="Phone" />      Continue with Phone number    </view>    <view class="footer-links">      <a href="#">Privacy Policy</a>      <a href="#">Help/Support</a>    </view>  </view></template><script>export default {  data() {    return {      email: '',      password: '',      showPassword: false    };  },  methods: {    togglePassword() {      this.showPassword = !this.showPassword;    },    signUp() {      alert(`Signing up with\nEmail: ${this.email}\nPassword: ${this.password}`);    },    loginWith(method) {      alert(`${method} login`);    }  }};</script><style scoped>body {  margin: 0;  font-family: 'Inter', sans-serif;  background-color: #000;  color: #F9F6E1;}.container {  width: 360px;  margin: auto;  position: relative;  padding: 40px 31px;}h1 {  color: #F4C654;  font-size: 30px;  font-weight: 700;  margin-bottom: 20px;  text-align: center;}.subtext {  text-align: center;  font-size: 14px;  font-weight: 500;  margin-bottom: 10px;}.subtext a {  color: #F4C654;  text-decoration: none;}label {  font-size: 10px;  font-weight: 300;  display: block;  margin-top: 20px;  margin-bottom: 4px;}input[type="email"],input[type="password"] {  width: 100%;  padding: 10px 16px;  background-color: #2D2C2F;  border: none;  border-radius: 93px;  color: #F9F6E1;  font-size: 14px;}.eye-icon {  position: absolute;  right: 40px;  top: 225px;  width: 20px;  height: 20px;  background-color: #F9F6E1;  border-radius: 50%;}button.primary {  width: 100%;  padding: 12px;  margin-top: 20px;  background-color: #F4C654;  border: none;  border-radius: 93px;  font-size: 16px;  font-weight: 500;  cursor: pointer;  color: black;  box-shadow: 0 4px 4px rgba(0, 0, 0, 0.25);}.divider {  text-align: center;  font-size: 12px;  margin: 24px 0;}.alt-login {  display: flex;  align-items: center;  background-color: #F9F6E1;  border-radius: 93px;  padding: 10px;  margin-bottom: 14px;  color: black;  font-weight: 500;  cursor: pointer;}.alt-login img {  width: 24px;  height: 24px;  margin-right: 12px;}.footer-links {  display: flex;  justify-content: space-between;  font-size: 10px;  margin-top: 20px;}.footer-links a {  color: #F9F6E1;  text-decoration: none;}</style>